import React, { useState } from 'react';
import AddStudentForm from './components/AddStudentForm';
import StudentList from './components/StudentList';

function App() {
  const [refreshKey, setRefreshKey] = useState(0);

  const handleStudentAdded = () => {
    setRefreshKey(prev => prev + 1);
  };

  return (
    <div className="min-h-screen bg-gray-100">
      <div className="container mx-auto px-4 py-8">
        <header className="text-center mb-8">
          <h1 className="text-4xl font-bold text-gray-900 mb-2">Student Portal</h1>
          <p className="text-gray-600">Manage and view students by department</p>
        </header>

        <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
          <div>
            <AddStudentForm onStudentAdded={handleStudentAdded} />
          </div>
          <div>
            <StudentList key={refreshKey} />
          </div>
        </div>

        <footer className="mt-12 text-center text-gray-500 text-sm">
          <p>&copy; 2024 Student Portal. Built with React, Go, and PostgreSQL.</p>
        </footer>
      </div>
    </div>
  );
}

export default App;
